import { useEffect, useState } from 'react';
import PortfolioList from '../portfolioList/PortfolioList';
import './Portfolio.scss';
import { featuredPortfolio, webPortfolio, mobilePortfolio, designPortfolio, contentPortfolio } from '../../data';

const Portfolio = () => {
  const [selected, setSelected] = useState("featured");
  const [data, setData] = useState([]);
  const list = [
    {
      id: "featured",
      title: "Featured",
    },
    {
      id: "web",
      title: "Web App",
    },
    {
      id: "mobile",
      title: "Mobile App",
    },
    {
      id: "design",
      title: "Design",
    },
    {
      id: "content",
      title: "Content",
    },
  ];

  useEffect(() => {
    switch(selected){
      case "featured":
        setData(featuredPortfolio);
        break
    }
  },[selected])
  return (
    <div className="portfolio" id="portfolio">
      <h1 className="port-title">portfolio</h1>
      <ul className="port-lists flex">
        {list.map((item) => (
          <PortfolioList 
          title={item.title}
          active={selected == item.id}
          setSelected={setSelected}
          id={item.id}
          />
        ))}
        {/* <li className="port-items active">featured</li>
        <li className="port-items">web app</li>
        <li className="port-items">mobile app</li>
        <li className="port-items">designing</li>
        <li className="port-items">branding</li> */}
      </ul>
      <div className="container flex">
        <div className="item flex">
          <img className="port-image" src="assets/port.jpg" />
          <h3 className="port-text">banking app</h3>
        </div>
        <div className="item flex">
           <img className="port-image" src="assets/port.jpg" />
          <h3 className="port-text">banking app</h3>
        </div>
        <div className="item flex">
           <img className="port-image" src="assets/port.jpg" />
          <h3 className="port-text">banking app</h3>
        </div>
        <div className="item flex">
           <img className="port-image" src="assets/port.jpg" />
          <h3 className="port-text">banking app</h3>
        </div>
        <div className="item flex">
           <img className="port-image" src="assets/port.jpg" />
          <h3 className="port-text">banking app</h3>
        </div>
        <div className="item flex">
           <img className="port-image" src="assets/port.jpg" />
          <h3 className="port-text">banking app</h3>
        </div>
      </div>
    </div>
  )
}

export default Portfolio;
